        //Flips a sprite upside down.
        public string[] ReturnFlippedSprite(string[] spriteToFlip)
        {
            int counter = 0;
            string[] flippedSprite = new string[spriteToFlip.Length];
            for (int i = spriteToFlip.Length; i > 0; i--)
            {
                flippedSprite[counter] = spriteToFlip[i-1];


                counter++;
            }

            return flippedSprite;
        }



